public class Position {
    
    protected int x,y;

    public Position() {
		// TODO Auto-generated constructor stub
	}
    
    public Position(int x, int y) {
        this.x=x;
        this.y=y;
    
    }

    public int getX() {
        return x;
    }

    public void setX(int x) {
        this.x = x;
    }

    public int getY() {
        return y;
    }

    public void setY(int y) {
        this.y = y;
    }
    
    
    
    
    
}
